package main

import (
	"fmt"
	"runtime"
	"time"
)

func printNumbers(n int) {
	for i := 0; i < 500; i++ {
		fmt.Print(n)
	}
}

func printNumbersWithRest(n int) {
	for i := 0; i < 500; i++ {
		fmt.Print(n)
		runtime.Gosched()
	}
}

func concurrentFunctionWithRest() {
	fmt.Println("\nConcurrent Code Execution with rest:")
	runtime.GOMAXPROCS(1)
	go printNumbersWithRest(0)
	go printNumbersWithRest(1)
	time.Sleep(time.Second)
}

func concurrentFunction() {
	fmt.Println("Concurrent Code Execution:")
	runtime.GOMAXPROCS(1)
	go printNumbers(0)
	go printNumbers(1)
	time.Sleep(time.Second)
}

func parallelFunction() {
	fmt.Println("\nParallel Code Execution:")
	runtime.GOMAXPROCS(12)
	go printNumbers(0)
	go printNumbers(1)
	time.Sleep(time.Second)
}

func main() {
	concurrentFunction()
	parallelFunction()
	concurrentFunctionWithRest()
}

